insert  into `t_content`(`id`,`cid`,`title`,`description`,`view`,`status`,`hits`,`good`,`createtime`,`modifytime`,`authorId`,`authorName`,`sorter`,`content`,`bold`,`italic`,`color`,`img`) values ('0f9b2b6ab3d94abd80350729d38daa52','c1b10bb1e6b144d498d4ff9e2dcfd4c1','JFinal IDE:JFinal极速开发Eclipse插件','JFinal IDE:JFinal极速开发Eclipse插件JFinal IDE:JFinal极速开发Eclipse插件JFinal IDE:JFinal极速开发Eclipse插件',1,'on','off','off','2017-09-01 22:52:44',NULL,'1','admin',2,'<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n	&nbsp;JFinal &nbsp;IDE是一个极速创建JFinal开发环境的Eclipse平台插件。小木学堂提供JFinal教程和支持。JFinal开发的微信小程序社区俱乐部<a href=\"http://www.wxappclub.com/\" target=\"_blank\" style=\"box-sizing:border-box;color:#337AB7;text-decoration:none;\">http://www.wxappclub.com</a>\n</p>\n<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n	&nbsp; &nbsp; 通过定制向导，配置JFinal项目属性，数据库参数等快速生成基于JFinal框架的动态Java Web项目模型。\n</p>\n<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n	&nbsp; &nbsp; 插件生成的项目模型按照普通java web动态项目构建，并且通过向导配置自动生成JFinal项目所需的web.xml、数据库配置文件、JFinalConfig子类等。\n</p>\n<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n	&nbsp; &nbsp; 插件目前支持Mysql、SQL Server、SQLite、oracle、H2等在内的众多数据库一键生成数据库相应配置，自动导入相关jar包。\n</p>\n<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n	&nbsp; &nbsp; 插件还提供了一键快速定位打开这些文件的功能。\n</p>\n<div>\n	<br />\n</div>','off','off',NULL,NULL);
insert  into `t_content`(`id`,`cid`,`title`,`description`,`view`,`status`,`hits`,`good`,`createtime`,`modifytime`,`authorId`,`authorName`,`sorter`,`content`,`bold`,`italic`,`color`,`img`) values ('5bae839955e8452c96d44b105bc9ba4e','c1b10bb1e6b144d498d4ff9e2dcfd4c1','JFinal-event（JFinal消息驱动插件）','JFinal-event（JFinal消息驱动插件）JFinal-event（JFinal消息驱动插件）JFinal-event（JFinal消息驱动插件）JFinal-event（JFinal消息驱动插件）',4,'on','off','off','2017-09-01 22:53:51',NULL,'1','admin',3,'<h1 class=\"jf-article-title\" style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;font-weight:500;line-height:1.1;color:#404040;font-size:30px;margin:0px;padding:20px 0px 5px;text-align:center;letter-spacing:1px;white-space:normal;\">\n	<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n		模仿的Spring中的消息事件：<a href=\"http://jinnianshilongnian.iteye.com/blog/1902886\" target=\"_blank\" style=\"box-sizing:border-box;color:#337AB7;text-decoration:none;\">详解Spring事件驱动模</a>\n	</p>\n	<p style=\"box-sizing:border-box;font-family:\'Microsoft YaHei\', 微软雅黑, \'Helvetica Neue\', Arial, 宋体, sans-serif;margin-top:0px;margin-bottom:16px;padding:0px;font-size:18px;color:#333333;line-height:30px;white-space:normal;background-color:#FEFEFE;\">\n		代码简单无任何第三方依赖，具体使用如下：\n	</p>\n<pre class=\"brush:java;toolbar:false prettyprint linenums prettyprinted\" style=\"box-sizing:border-box;word-break:break-all;word-wrap:break-word;border:1px solid #CCCCCC;font-family:Consolas, Monaco, \'Courier New\', \'Microsoft Yahei\', 微软雅黑, monospace;margin-top:0px;margin-bottom:14px;padding:0.5em;font-size:16px;line-height:20px;border-radius:2px;white-space:pre-wrap;color:#333333;background-color:#2B2B2B;\">\n	<ol class=\"linenums\" style=\"box-sizing:border-box;margin:0px;padding:0px 0px 0px 35px;color:#555555;\">\n		\n		<li class=\"L0\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;初始化插件</span>\n		</li>\n\n		<li class=\"L1\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"typ\" style=\"box-sizing:border-box;color:#A8B6C5;\">EventPlugin</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;plugin&nbsp;</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">=</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span><span class=\"kwd\" style=\"box-sizing:border-box;color:#CB7832;\">new</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span><span class=\"typ\" style=\"box-sizing:border-box;color:#A8B6C5;\">EventPlugin</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">();</span>\n		</li>\n\n		<li class=\"L2\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;开启异步，默认同步。或者使用`threadPool(ExecutorService&nbsp;executorService)`自定义线程池。</span>\n		</li>\n\n		<li class=\"L3\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">plugin</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">.</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">async</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">();</span>\n		</li>\n\n		<li class=\"L4\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span>\n		</li>\n\n		<li class=\"L5\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;设置扫描jar包，默认不扫描</span>\n		</li>\n\n		<li class=\"L6\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">plugin</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">.</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">scanJar</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">();</span>\n		</li>\n\n		<li class=\"L7\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;设置监听器默认包，默认全扫描</span>\n		</li>\n\n		<li class=\"L8\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">plugin</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">.</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">scanPackage</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">(</span><span class=\"str\" style=\"box-sizing:border-box;color:#6A8659;\">\"net.dreamlu\"</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">);</span>\n		</li>\n\n		<li class=\"L9\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span>\n		</li>\n\n		<li class=\"L0\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;启动插件,用于main方法启动，jfinal中不需要，添加插件即可。</span>\n		</li>\n\n		<li class=\"L1\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">plugin</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">.</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">start</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">();</span>\n		</li>\n\n		<li class=\"L2\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span>\n		</li>\n\n		<li class=\"L3\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;发送第一个消息</span>\n		</li>\n\n		<li class=\"L4\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"typ\" style=\"box-sizing:border-box;color:#A8B6C5;\">EventKit</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">.</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">post</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">(</span><span class=\"kwd\" style=\"box-sizing:border-box;color:#CB7832;\">new</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span><span class=\"typ\" style=\"box-sizing:border-box;color:#A8B6C5;\">Test1Event</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">(</span><span class=\"str\" style=\"box-sizing:border-box;color:#6A8659;\">\"hello1\"</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">));</span>\n		</li>\n\n		<li class=\"L5\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"com\" style=\"box-sizing:border-box;color:#7F7F7F;\">//&nbsp;发送带tag的消息</span>\n		</li>\n\n		<li class=\"L6\" style=\"box-sizing:border-box;margin:0px;padding:0px;\">\n			<span class=\"typ\" style=\"box-sizing:border-box;color:#A8B6C5;\">EventKit</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">.</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">post</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">(</span><span class=\"str\" style=\"box-sizing:border-box;color:#6A8659;\">\"save\"</span><span class=\"pun\" style=\"box-sizing:border-box;color:#B9B9B9;\">,</span><span class=\"pln\" style=\"box-sizing:border-box;color:#A8B6C5;\">&nbsp;</span><span class=\"kwd\" style=\"box-sizing: