DROP TABLE IF EXISTS `t_channel`;

CREATE TABLE `t_channel` (
  `id` varchar(36) NOT NULL COMMENT '主键',
  `name` varchar(50) default NULL COMMENT '名称',
  `parent` int(11) default NULL COMMENT '父级id',
  `url` varchar(255) default NULL COMMENT 'url地址',
  `type` varchar(10) default '1',
  `hide` varchar(10) default '0',
  `sorter` int(11) default '0',
  `target` varchar(10) default '0',
  `loaded` varchar(10) default '1',
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='频道';

